import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import { w3cwebsocket as W3cWebSocket } from 'websocket';
import 'antd/dist/antd.css';
import { Input, Typography } from 'antd';
import './index.css'


const client = new W3cWebSocket('ws://127.0.0.1:8000');
const { Search } = Input;
const { Text } = Typography;

export const App = () => {

    const [userInfo, setUser] = useState({
        userName: '',
        isLoggedIn: false
    });
    const [usermessage, setUserMessage] = useState([]);
    const [searchval, setSearchVal] = useState("");


    useEffect(() => {
        client.onopen = () => {
            console.log('Websocket Client connected');
        };
        client.onmessage = (message) => {
            const dataFromServer = JSON.parse(message.data);
            console.log('get replay', dataFromServer);

            if (dataFromServer.type === 'message') {
                setUserMessage([
                    ...usermessage,
                    {
                        newMsg: dataFromServer.msg,
                        newUser: dataFromServer.currUser
                    }
                ])
            }


        };
        console.log('new', usermessage)
    }, [usermessage]);

    const onButtonClicked = (value) => {
        client.send(JSON.stringify({
            type: "message",
            msg: value,
            currUser: userInfo.userName
        }));
    }

    return (
        <div className="main">
            {
                userInfo.isLoggedIn ?
                    <div>
                        <div className="title">
                            <Text id="main-heading" type="secondary" style={{ fontSize: '36px' }}>Websocket Chat: {userInfo.userName}</Text>
                        </div>
                        <div className="bottom">
                            <Search
                                placeholder="Enter UserName"
                                enterButton="Login"
                                size="Large"
                                onChange={(e) => setSearchVal(e.target.value)}
                                onSearch={value => setUser({ isLoggedIn: true, userName: value })}
                            />
                        </div>
                    </div>
                    : <div style={{ padding: '200px 40px' }}>
                        <Search
                            placeholder="Enter UserName"
                            enterButton="Login"
                            size="Large"
                            onSearch={value => setUser({ isLoggedIn: true, userName: value })}
                        />
                    </div>
            }

        </div>
    )
}

ReactDOM.render(<App />, document.getElementById('root'));
