import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import { w3cwebsocket as W3cWebSocket } from 'websocket';
import 'antd/dist/antd.css';
import { Input } from 'antd';
import Item from 'antd/lib/list/Item';

const client = new W3cWebSocket('ws://127.0.0.1:8000');
const { Search } = Input;

export const App = () => {

    const [userInfo, setUser] = useState({
                                userName: '',
                                isLoggedIn: false,
                                message: [{
                                    msg: 'i',
                                    user: 'v'
                                }]});

   
    useEffect(() => {
        client.onopen = () => {
            console.log('Websocket Client connected');
        };
        client.onmessage = (smessage) => {
            const dataFromServer = JSON.parse(smessage.data);
            console.log('get replay', dataFromServer);
            userInfo.message.map(item => console.log(item.msg))

           if (dataFromServer.type === 'message') {
               console.log("if success")
                setUser(
                    {...userInfo, message: [...userInfo.message,{
                        msg: dataFromServer.msg,
                        user: dataFromServer.user
                    }]}
                )
            }
        };
    });

    const onButtonClicked = (value) => {
        client.send(JSON.stringify({
            type: "message",
            msg: value,
            currUser: userInfo.userName
        }));
    }

    return (
        <div className="main">
            {
                userInfo.isLoggedIn ? 
                <div>
                 <button onClick={() => onButtonClicked('hi')}>Send message</button>
                 
                </div>
                : <div style={{padding: '200px 40px'}}>
                    <Search 
                        placeholder = "Enter UserName"
                        enterButton = "Login"
                        size = "Large"
                        onSearch = { value => setUser({isLoggedIn: true, userName: value})}
                    />
                </div> 
            }
            
        </div>
    )
}

ReactDOM.render(<App />, document.getElementById('root'));
